        const baseUrl = "https://mxonlive.xyz/";
        const defaultCategory = "Entertainment";

        let player = null;

        // Initialize Video.js
        document.addEventListener('DOMContentLoaded', function () {
            player = videojs('my-video', {
                fluid: true,
                responsive: true,
                aspectRatio: "16:9",
                controls: true,
                autoplay: false,
                preload: 'auto',
                bigPlayButton: true,
                html5: {
                    vhs: {
                        overrideNative: !videojs.browser.IS_SAFARI,
                        withCredentials: false,
                        bandwidth: 3000000,
                        limitRenditionByPlayerDimensions: false
                    },
                    nativeAudioTracks: false,
                    nativeVideoTracks: false
                }
            });

            player.on('error', function() {
                console.error('Video.js Error:', player.error());
            });
        });

        // Mouse wheel scroll for categories
        const scrollContainer = document.getElementById("categoryScroll");
        if (scrollContainer) {
            scrollContainer.addEventListener("wheel", (evt) => {
                evt.preventDefault();
                scrollContainer.scrollLeft += evt.deltaY;
            });
        }

        function playStream(id, element) {
            document.querySelectorAll('.channel-card').forEach(el => el.classList.remove('active-channel'));
            if (element) element.classList.add('active-channel');

            const streamUrl = baseUrl + "stream.php/" + id + ".m3u8";

            if (player) {
                player.src({
                    src: streamUrl,
                    type: 'application/x-mpegURL'
                });

                player.ready(function() {
                    player.play().catch(e => {
                        console.log("Play failed:", e);
                    });
                });
            }

            if (window.innerWidth < 992) {
                window.scrollTo({ top: 0, behavior: 'smooth' });
            }
        }

        function renderChannels(category) {
            const container = document.getElementById('channel-container');
            container.innerHTML = '';

            let firstChannelId = null;
            let firstChannelElement = null;

            allChannels.forEach(ch => {
                let catName = ch.category_name || 'Others';
                if (catName === category) {
                    let div = document.createElement('div');
                    div.className = 'channel-card';
                    div.title = ch.name;
                    div.onclick = function() { playStream(ch.id, this); };

                    let img = document.createElement('img');
                    img.src = ch.logo;
                    img.loading = "lazy";
                    img.onerror = function() { this.src = 'https://via.placeholder.com/100?text=TV'; };

                    div.appendChild(img);
                    container.appendChild(div);

                    if (!firstChannelId) {
                        firstChannelId = ch.id;
                        firstChannelElement = div;
                    }
                }
            });

            // Auto-play first channel of default category
            if (category === defaultCategory && firstChannelId) {
                setTimeout(() => {
                    if (player && firstChannelElement) {
                        playStream(firstChannelId, firstChannelElement);
                    }
                }, 1200);
            }
        }

        function filterChannels(category, tabElement) {
            document.querySelectorAll('.nav-link').forEach(el => el.classList.remove('active'));
            tabElement.classList.add('active');
            renderChannels(category);
        }

        document.addEventListener('DOMContentLoaded', () => {
            renderChannels(defaultCategory);
        });